        const now = new Date(),startOfYear = new Date(now.getFullYear(), 0, 1),startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1),startOfWeek = new Date(now.getFullYear(), now.getMonth(), now.getDate() - now.getDay()),startOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate());
